//import logo from './logo.svg';
import './App.css';
//import SignIn from "./signin/signin"

import React, { useRef, useState } from 'react';
import firebase from 'firebase/app';
import 'firebase/firestore';
import 'firebase/auth';

import { useAuthState } from 'react-firebase-hooks/auth';
import { useCollectionData } from 'react-firebase-hooks/firestore';

import HeaderComponent from "./components/header/header"
import Layout from "./components/layout/layout"
import Card from "./components/card/card"

import { StyledLink } from "./components/header/headerStyles"

import {Container, 
        Section,
        DivChatArea,
        DivChatHeader,
        DivMessageSections,
        TextMessage,
        DivChatControls,
        Textarea,
        Button} from "./styles/chatStyles"

import {ContainerSignIn} from "./components/card/cardStyles"

firebase.initializeApp({
    apiKey: "AIzaSyCW1V-XomyFj_pfzJg1WVxeVWJB7b7ARZI",
    authDomain: "chat-for-testapp.firebaseapp.com",
    databaseURL: "https://chat-for-testapp.firebaseio.com",
    projectId: "chat-for-testapp",
    storageBucket: "chat-for-testapp.appspot.com",
    messagingSenderId: "285672943459",
    appId: "1:285672943459:web:6942bbd53520a9ccdaed56",
    measurementId: "G-VS992604ML"
})


const auth = firebase.auth()
const firestore = firebase.firestore()


function App() {

  const [user] = useAuthState(auth)

  return (
    <div className="App">

      {user ? 
        ( <Layout>
            <HeaderComponent>
              <SignOut />
            </HeaderComponent>

            <ChatRoom />
          </Layout>) : (<SignIn/>)}  
    </div>
  );
}

const SignIn = () => {

  const signInWithGoogle = () => {
    const provider = new firebase.auth.GoogleAuthProvider();
    auth.signInWithPopup(provider);
  }

  return (
    <Layout>
      <HeaderComponent/>

      <ContainerSignIn>
        <Card>
          <Button onClick={signInWithGoogle}>Sign in with Google</Button>
        </Card>
      </ContainerSignIn>
    </Layout>
  )
}


function SignOut() {
  return auth.currentUser && (

    <StyledLink onClick={() => auth.signOut()}>Sign Out</StyledLink>
  )
}


function ChatRoom () {


  const dummy = useRef()

  const messagesRef = firestore.collection('messages');
  //console.log(messagesRef)
  const query = messagesRef.orderBy('createdAt').limit(25);

  const [messages] = useCollectionData(query, {idField: 'id'});

  console.log(messages)


  const [formValue, setFormValue] = useState('');


  const sendMessage = async(e) => {

    e.preventDefault();

    const { uid, photoURL } = auth.currentUser;

    await messagesRef.add({
      text: formValue,
      createdAt: firebase.firestore.FieldValue.serverTimestamp(),
      uid, 
      photoURL
    });

    setFormValue('');

    dummy.current.scrollIntoView({ behavior: 'smooth' });
  }



  const person = messages ? messages.filter(message => message.uid !== auth.currentUser.uid)[0] : null


  return (
    <Section>
      <div className="users">
            {   person &&
                //messages.filter(message => message.uid !== auth.currentUser.uid).map(message => {
                    //return (
                    <div key={person.uid} className="name">
                        <div className="pic">
                          <img src="https://a.slack-edge.com/66f9/img/avatars-teams/ava_0001-34.png"/>
                        </div>
                        <div className="userinfo"> 
                            <span style={{ fontWeight: 500 }}>{person.uid}</span>
                            <span>online</span>
                            {/*<span>online</span>*/}
                        </div>
                    </div>//)
            //})
            }
    </div>

    <DivChatArea>
            <DivChatHeader></DivChatHeader>
                <DivMessageSections>
                    <div style={{ textAlign: "left"}}>
                        <TextMessage>
                          {messages && messages.map(msg => <ChatMessage key={msg.id} message={msg} />)}
                          <div ref={dummy}></div>
                        </TextMessage>
                    </div>
                </DivMessageSections>
                <DivChatControls onSubmit={sendMessage}>
                    <Textarea 
                      placeholder="Remember, be nice!"
                      value={formValue} 
                      onChange={(e) => setFormValue(e.target.value)} 
                    />
                    <Button type="submit">Send</Button>
                </DivChatControls>
        </DivChatArea>

      {/*<div className="">
        <div>
          {messages && messages.map(msg => <ChatMessage key={msg.id} message={msg} />)}

          <div ref={dummy}></div>

        </div>

        <form onSubmit={sendMessage}> 

          <input value={formValue} onChange={(e) => setFormValue(e.target.value)} />

          <button type="submit">Send</button>


        </form>
          </div>*/}
    </Section>
  ) 

}

function ChatMessage(props) {
  const { text, uid, photoURL } = props.message;

  const messageClass = uid === auth.currentUser.uid ? 'sent' : 'received';

  return ( 

    <div className={`message ${messageClass}`}>
      <img src={photoURL} />
      <p>{text}</p>
    </div>

  )
}

export default App;
