//import logo from './logo.svg';
import './App.css';
//import SignIn from "./signin/signin"

import React, { useEffect, useRef, useState } from 'react';
import firebase from 'firebase/app';
import 'firebase/firestore';
import 'firebase/auth';

import { useAuthState } from 'react-firebase-hooks/auth';
import { useCollectionData } from 'react-firebase-hooks/firestore';

import HeaderComponent from "./components/header/header"
import Layout from "./components/layout/layout"
import Card from "./components/card/card"

import { StyledLink, HeaderButton } from "./components/header/headerStyles"

import {  Container, 
          Section,
          DivChatArea,
          DivChatHeader,
          DivMessageSections,
          TextMessage,
          DivChatControls,
          Textarea,
          Button,
          DivUsers, 
          DivName,
          DivPic,
          Image,
          DivUserInfo,
          SpanOnline,
          SpanOffline} from "./styles/chatStyles"

import {  ContainerSignIn,
          CardButton,
          Form } from "./components/card/cardStyles"

firebase.initializeApp({
    apiKey: "AIzaSyCW1V-XomyFj_pfzJg1WVxeVWJB7b7ARZI",
    authDomain: "chat-for-testapp.firebaseapp.com",
    databaseURL: "https://chat-for-testapp.firebaseio.com",
    projectId: "chat-for-testapp",
    storageBucket: "chat-for-testapp.appspot.com",
    messagingSenderId: "285672943459",
    appId: "1:285672943459:web:6942bbd53520a9ccdaed56",
    measurementId: "G-VS992604ML"
})


const auth = firebase.auth()
const firestore = firebase.firestore()


function App() {

  const [user] = useAuthState(auth)

  return (
    <div className="App">

      {user ? 
        ( <Layout>
            <HeaderComponent>
              <SignOut />
            </HeaderComponent>

            <ChatRoom />
          </Layout>) : (<SignIn/>)}  
    </div>
  );
}

const SignIn = () => {

  const signInWithGoogle = () => {
    const provider = new firebase.auth.GoogleAuthProvider();
    auth.signInWithPopup(provider);
  }

  return (
    <Layout>
      <HeaderComponent/>

      <ContainerSignIn>
        <Card>
            <CardButton onClick={signInWithGoogle}>Sign in with Google</CardButton>
        </Card>
      </ContainerSignIn>
    </Layout>
  )
}


function SignOut() {
  return auth.currentUser && (

    <StyledLink onClick={() => auth.signOut()}>Logout</StyledLink>
  )
}


function ChatRoom () {
  const [formValue, setFormValue] = useState('');
  //const [inputValue, setInputValue] = useState(false)
  const [chatStarted, setChatStarted] = useState(false)

  const dummy = useRef()                      // Reference to div under messages


  const messagesRef = firestore.collection('messages');
  const query = messagesRef.orderBy('createdAt').limit(25);

  const [messages] = useCollectionData(query, {idField: 'id'});

  console.log(messages)




  const sendMessage = async(e) => {

    e.preventDefault();

    const { uid, photoURL } = auth.currentUser;

    await messagesRef.add({
      text: formValue,
      createdAt: firebase.firestore.FieldValue.serverTimestamp(),
      uid, 
      photoURL
    });

    setFormValue('');

    dummy.current.scrollIntoView({ behavior: 'smooth' });
  }

  const messagesEndRef = useRef(null)         // Make new ref for scrolldown function

  const scrollToBottom = () => {
    messagesEndRef.current.scrollIntoView({ behavior: 'smooth' })
  }

  useEffect(() => {

    scrollToBottom();

  }, [messages])



  const User = (props) => {

    const { person } = props

    return (
      <DivName>
          <DivPic >
            <Image src="https://a.slack-edge.com/66f9/img/avatars-teams/ava_0001-34.png"/>
          </DivPic>
          <DivUserInfo> 
              <span style={{ fontWeight: 500 }}>{person.uid}</span>
              <SpanOnline />
              {/*<span>online</span>*/}
          </DivUserInfo>
      </DivName>
    )
  }



  const initChat = (person) => {
    setChatStarted(true)
    console.log(chatStarted)
  }


  const person = messages ? messages.filter(message => message.uid !== auth.currentUser.uid)[0] : null  // Weakness: Depends on messages sent


  return (
    <Section>
      <DivUsers>
            {   person && <User key={person.uid} 
                                person={person}
                                onClick={initChat}
                          />
                //messages.filter(message => message.uid !== auth.currentUser.uid).map(message => {
                    //return (
                    //)
            //})
            }
    </DivUsers>

    <DivChatArea>
            {/*<DivChatHeader></DivChatHeader>*/}
                <DivMessageSections>
                    <div style={{ textAlign: "left" }}>
                        <TextMessage>
                          {messages && messages.map(msg => <ChatMessage key={msg.id} message={msg} />)}
                          <div ref={dummy}></div>
                          {<div ref={messagesEndRef}></div>}
                        </TextMessage>
                    </div>
                </DivMessageSections>
                <DivChatControls onSubmit={sendMessage}>
                    <Textarea 
                      placeholder="Happy chatting!"
                      value={formValue} 
                      onChange={(e) => setFormValue(e.target.value)} 
                    />
                    <Button type="submit" disabled={formValue===''}>Send</Button>
                </DivChatControls>
        </DivChatArea>

      {/*<div className="">
        <div>
          {messages && messages.map(msg => <ChatMessage key={msg.id} message={msg} />)}

          <div ref={dummy}></div>

        </div>

        <form onSubmit={sendMessage}> 

          <input value={formValue} onChange={(e) => setFormValue(e.target.value)} />

          <button type="submit">Send</button>


        </form>
          </div>*/}
    </Section>
  ) 

}

function ChatMessage(props) {
  const { text, uid, photoURL } = props.message;

  const messageClass = uid === auth.currentUser.uid ? 'sent' : 'received';

  return ( 

    <div className={`message ${messageClass}`}>
      <img src={photoURL} />
      <p>{text}</p>
    </div>

  )
}

export default App;
